// Step 1: Find all nodes with t:1 or t:2
MATCH (n:Node {t : 2})

// Step 2: Swap n.t between 1 and 2
SET n.t = CASE n.t WHEN 1 THEN 2 ELSE 1 END

// Step 3: For each "Wire" neighbor, swap their t if 1 or 2
WITH n
MATCH (n)-[w:Wire]-()
WHERE w.t IN [1, 2]
SET w.t = CASE w.t WHEN 1 THEN 2 ELSE 1 END

