// Match all pairs with more than two wires between them
MATCH (n1:Node {t: 1})-[w:Wire]-(n2:Node {t: 2})
WITH n1, n2, collect(w) AS wires, count(w) AS n_wires

// Calculate how many to keep (either 0 or 1)
WITH n1, n2, wires, n_wires, n_wires % 2 AS keep_n
// Get the wires to delete
WITH wires[..(n_wires - keep_n)] AS wires_to_delete
UNWIND wires_to_delete AS w_to_delete
DELETE w_to_delete

