  // Match two consecutive nodes of type t:3
  MATCH (a {t:3})-[r]-(b {t:3})
  WHERE id(a) < id(b) // to avoid double-counting the same pair
  // Create the intermediate node of type t:1 with phase 0
  CREATE (a)-[:Wire {t:1}]->(:Node {t:1, phase:0})-[:Wire {t:1}]->(b)
  // Remove the old direct edge
  DELETE r
}

CALL {
  // Match pattern with t:3 node of degree 2
  MATCH (n1:Node)-[w1]-(h:Node {t: 3})-[w2]-(n2:Node)
  WHERE id(n1) < id(n2) // prevent double processing

  // Compute the t value for the new wire
  WITH n1, n2, h,
      w1.t AS t1, w2.t AS t2
  WITH n1, n2, h, t1, t2,
      CASE
          WHEN t1 = 1 AND t2 = 1 THEN 2
          WHEN (t1 = 1 AND t2 = 2) OR (t1 = 2 AND t2 = 1) THEN 1
          WHEN t1 = 2 AND t2 = 2 THEN 2
          ELSE 1 // fallback, in case types are not 1 or 2
      END AS new_t

  // Create the new wire, remove old wires and the hadamard node
  MERGE (n1)-[:Wire {t: new_t}]->(n2)
  WITH h
  DETACH DELETE h
}

